import React, { useEffect, useState } from 'react'
import supabase from '../utils/supabaseClient'

export default function Catalog() {
  const [books, setBooks] = useState([])
  const [userId, setUserId] = useState(null)

  useEffect(() => {
    const fetchUserAndBooks = async () => {
      const { data: { user } } = await supabase.auth.getUser()
      setUserId(user?.id)

      // Step 1: Get user's location
      const { data: customer } = await supabase
        .from('customerinfo')
        .select('CustomerLocation')
        .eq('CustomerID', user?.id)
        .single()

      const userLocation = customer?.CustomerLocation

      // Step 2: Get rented ISBNs
      const { data: history } = await supabase
        .from('circulationhistory')
        .select('ISBN13')
        .eq('MemberID', user?.id)

      const rentedISBNs = history?.map(h => h.ISBN13) || []

      // Step 3: Get available books
      const { data: availableCopies } = await supabase
        .from('copyinfo')
        .select('ISBN13')
        .eq('CopyLocation', userLocation)
        .eq('Available', true)

      const availableISBNs = [...new Set(availableCopies.map(copy => copy.ISBN13))]

      // Step 4: Get books in catalog that are available and not already rented
      const { data: catalog } = await supabase
        .from('catalog')
        .select('*')
        .in('ISBN13', availableISBNs)

      const filtered = catalog?.filter(book => !rentedISBNs.includes(book.ISBN13))
      setBooks(filtered)
    }

    fetchUserAndBooks()
  }, [])

  return (
    <div className="p-4">
      <h2 className="text-2xl font-bold mb-4">Available Books</h2>
      <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
        {books.map(book => (
          <div key={book.ISBN13} className="bg-white p-4 rounded shadow">
            <img src={book.Thumbnail} alt={book.Title} className="w-full h-40 object-cover mb-2" />
            <h3 className="font-bold">{book.Title}</h3>
            <p className="text-sm text-gray-600">{book.Authors}</p>
            <p className="text-sm mt-1">{book.Description?.slice(0, 100)}...</p>
          </div>
        ))}
      </div>
    </div>
  )
}
